frappe.listview_settings["Item"] = {
    hide_name_column: true,
    add_fields: ["item_code"],

    button: {
        show: function(doc) {
            return '<i class="fa fa-edit"></i> Prices';
        },
        get_label: function() {
            return __('<i class="fa fa-edit"></i> Prices', null, "Access");
        },
        get_description: function(doc) {
            return __("Add/Edit Prices of " + doc.item_code);
        },
        action: function(doc) {
            console.log("Action Clicked 1");

            frappe.db.get_list('Item Price', {
                fields: ['name', 'item_name', 'price_list', 'price_list_rate'],
                filters: {
                    item_code: doc.item_code
                },
                limit: null
            }).then(function(prices) {
                console.log("Item Prices: ", prices);
                let d = new frappe.ui.Dialog({
                    title: doc.item_code + ': Item Prices',
                    fields: [{
                        label: 'Item Price Table',
                        fieldname: 'prices_table',
                        fieldtype: 'Table',
                        cannot_add_rows: 1,
                        cannot_delete_rows: 1,
                        in_place_edit: 1,
                        fields: [
                            {
                                label: 'Name',
                                fieldname: 'name',
                                fieldtype: 'Data',
                                hidden: 1,
                            },
                            {
                                label: 'Item Name',
                                fieldname: 'item_name',
                                fieldtype: 'Data',
                                read_only: 1,
                                in_list_view: 1,
                            },
                            {
                                label: 'Price List',
                                fieldname: 'price_list',
                                fieldtype: 'Link',
                                options: "Price List",
                                read_only: 1,
                                in_list_view: 1,
                            },
                            {
                                label: 'Price List Rate',
                                fieldname: 'price_list_rate',
                                fieldtype: 'Currency',
                                in_list_view: 1,
                            }
                        ],
                        data: prices
                    }, ],
                    size: 'large', // small, large, extra-large 
                    primary_action_label: 'Update Price',
                    primary_action: function() {
                        var data = d.get_values().prices_table;
                        console.log("Update Price Data", data);
                        if (data) {
                            var updated = false;
                            data.forEach(function(price) {
                                frappe.call({
                                    method: "frappe.client.set_value",
                                    args: {
                                        doctype: "Item Price",
                                        name: price.name,
                                        fieldname: 'price_list_rate',
                                        value: price.price_list_rate
                                    },
                                    callback: function(response) {
                                        if (!updated && !response.exc) {
                                            updated = true;
                                            frappe.msgprint(__('Item: <b>' + doc.item_code + '</b> prices updated successfully'));
                                        }
                                    }
                                });
                            });
                        }
                        d.hide();
                    },
                    secondary_action_label: __("Add Price"),
                    secondary_action: function() {
                        d.hide();
                        frappe.new_doc("Item Price", {
                            item_code: doc.item_code
                        });
                    },
                });
                d.show();
            });
        },
    },

    refresh: function(listview) {
        $("button.btn.btn-action.btn-default.btn-xs").addClass("btn-info").removeClass("btn-default");
    },

    onload: function(listview) {
        listview.page.add_action_item(__('<i class="fa fa-edit"></i> Prices'), () => {
            console.log("--Action Clicked--");
            let checked_items = listview.get_checked_items(true);
            console.log("--checked_items--", checked_items);

            frappe.db.get_list('Item Price', {
                fields: ['name', 'item_name', 'price_list', 'price_list_rate'],
                filters: {
                    item_code: ['IN', checked_items]
                },
                order_by: 'item_name',
                limit: null
            }).then(function(prices) {
                console.log("Item Prices: ", prices);
                let d = new frappe.ui.Dialog({
                    title: 'Bulk Update Item Prices',
                    fields: [{
                        label: 'Item Price Table',
                        fieldname: 'prices_table',
                        fieldtype: 'Table',
                        cannot_add_rows: 1,
                        cannot_delete_rows: 1,
                        // in_place_edit: 1,
                        fields: [
                            {
                                label: 'Name',
                                fieldname: 'name',
                                fieldtype: 'Data',
                                hidden: 1,
                            },
                            {
                                label: 'Item Name',
                                fieldname: 'item_name',
                                fieldtype: 'Data',
                                read_only: 1,
                                in_list_view: 1,
                            },
                            {
                                label: 'Price List',
                                fieldname: 'price_list',
                                fieldtype: 'Link',
                                options: "Price List",
                                read_only: 1,
                                in_list_view: 1,
                            },
                            {
                                label: 'Price List Rate',
                                fieldname: 'price_list_rate',
                                fieldtype: 'Currency',
                                in_list_view: 1,
                            }
                        ],
                        data: prices
                    }, ],
                    size: 'large', // small, large, extra-large 
                    primary_action_label: 'Update Price',
                    primary_action: function() {
                        var data = d.get_values().prices_table;
                        console.log("Update Price Data", data);
                        if (data) {
                            var updated = false;
                            data.forEach(function(price) {
                                frappe.call({
                                    method: "frappe.client.set_value",
                                    args: {
                                        doctype: "Item Price",
                                        name: price.name,
                                        fieldname: 'price_list_rate',
                                        value: price.price_list_rate
                                    },
                                    callback: function(response) {
                                        if (!updated && !response.exc) {
                                            updated = true;
                                            frappe.msgprint(__('Item prices updated successfully'));
                                        }
                                    }
                                });
                            });
                        }
                        d.hide();
                    },
                    secondary_action_label: __("Add Price"),
                    secondary_action: function() {
                        d.hide();
                        let add_dialog = new frappe.ui.Dialog({
                            title: 'Add Item Prices',
                            fields: [{
                                label: 'Add Item Price Table',
                                fieldname: 'add_prices_table',
                                fieldtype: 'Table',
                                // cannot_add_rows: 1,
                                // cannot_delete_rows: 1,
                                // in_place_edit: 1,
                                fields: [
                                    {
                                        label: 'Item Code',
                                        fieldname: 'item_code',
                                        fieldtype: 'Link',
                                        options: "Item",
                                        // read_only: 1,
                                        in_list_view: 1,
                                    },
                                    {
                                        label: 'Price List',
                                        fieldname: 'price_list',
                                        fieldtype: 'Link',
                                        options: "Price List",
                                        // read_only: 1,
                                        in_list_view: 1,
                                    },
                                    {
                                        label: 'Price List Rate',
                                        fieldname: 'price_list_rate',
                                        fieldtype: 'Currency',
                                        in_list_view: 1,
                                    }
                                ],
                            }, ],
                            size: 'large', // small, large, extra-large 
                            primary_action_label: 'Save',
                            primary_action: function() {
                                var add_data = add_dialog.get_values().add_prices_table;

                                if (add_data && add_data.length > 0) {
                                    console.log("Update Price Data", add_data);
                                    var updated = false;
                                    add_data.forEach(function(price) {
                                        frappe.call({
                                            method: "frappe.client.insert",
                                            args: {
                                                doc: {
                                                    doctype: "Item Price",
                                                    item_code: price.item_code,
                                                    price_list: price.price_list,
                                                    price_list_rate: price.price_list_rate
                                                },
                                                callback: function(response) {
                                                    if (!updated && !response) {
                                                        updated = true;
                                                        frappe.msgprint(__('Item Prices added successfully.'));
                                                    }
                                                }
                                            }
                                        });
                                    });
                                    add_dialog.hide();
                                } else {
                                    frappe.msgprint("Please add ad least one Item Price.");
                                }
                            }
                        });
                        add_dialog.show();
                    },
                });
                d.show();
            });
        });
    }
};

let mask;

jQuery.getScript("https://unpkg.com/imask", () => {
    mask = IMask.createMask({
        mask: "+55 00 0000[0]-0000"
    })
})

frappe.ui.form.on('Address', {
	validate(frm) {
	    if (frm.doc.phone) {
            mask.resolve(frm.doc.phone);
            frm.doc.phone = mask.value;
            frm.refresh_field("phone");
	    }
	}
})

frappe.ui.form.on('Item', {
	validate(frm) {
	    var ncm = frm.fields_dict.nf_ncm.get_value();
	    if (ncm) {
	        frm.fields_dict.nf_ncm.set_value(formataNCM(ncm));
	        if (frm.fields_dict.nf_ncm.get_value().length > 10) {
	            msgprint('C칩digo NCM Inv치lido');
	            frappe.validated = false;
	        }
	    }
	}
})

function formataNCM(ncm){
    return ncm.replace(/[^\d]/g, "").replace(/(\d{4})(\d{2})(\d{2})/, "$1.$2.$3");
}

let mask;

jQuery.getScript("https://unpkg.com/imask", () => {
    mask = IMask.createMask({
        mask: "+55 00 0000[0]-0000"
    })
})

frappe.ui.form.on('Contact', {
	validate(frm) {
	    window.shirkit = frm;
	    console.log(1);
	    if (frm.fields_dict.phone_nos.grid.grid_rows) {
	        console.log(2);
	        frm.fields_dict.phone_nos.grid.grid_rows.forEach((el) => {
	            console.log(3);
	            mask.resolve(el.doc.phone);
	            el.doc.phone = mask.value;
	            el.refresh_field("phone");
	        })
	    }
	}
})

frappe.ui.form.on('Supplier', {
	validate(frm) {
	    if (frm.doc.country?.toLowerCase() !== "brazil")
	        return;

	    var tax = frm.fields_dict.tax_id.get_value();
	    if (tax) {
	        if (validarCPF(tax))
	            frm.fields_dict.tax_id.set_value(formataCPF(tax));
	        else if (validarCNPJ(tax))
	            frm.fields_dict.tax_id.set_value(formataCNPJ(tax));
	        else {
	            msgprint('CPF/CNPJ Inv치lido');
	            frappe.validated = false;
	        }
	    }
	}
})

function formataCPF(cpf){
    cpf = cpf.replace(/[^\d]/g, "");
    return cpf.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, "$1.$2.$3-$4");
}

function formataCNPJ(str){
    str = str.replace(/[^\d]/g, "");
    return str.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, "$1.$2.$3/$4-$5");
}

function validarCNPJ(cnpj) {

    cnpj = cnpj.replace(/[^\d]+/g,'');

    if(cnpj == '' || cnpj.length != 14) return false;

    // Valida DVs
    tamanho = cnpj.length - 2
    numeros = cnpj.substring(0,tamanho);
    digitos = cnpj.substring(tamanho);
    soma = 0;
    pos = tamanho - 7;
    for (i = tamanho; i >= 1; i--) {
      soma += numeros.charAt(tamanho - i) * pos--;
      if (pos < 2)
            pos = 9;
    }
    resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;
    if (resultado != digitos.charAt(0))
        return false;

    tamanho = tamanho + 1;
    numeros = cnpj.substring(0,tamanho);
    soma = 0;
    pos = tamanho - 7;
    for (i = tamanho; i >= 1; i--) {
      soma += numeros.charAt(tamanho - i) * pos--;
      if (pos < 2)
            pos = 9;
    }
    resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;
    if (resultado != digitos.charAt(1))
          return false;

    return true;

}

function validarCPF(cpf) {	
	cpf = cpf.replace(/[^\d]+/g,'');	
	if(cpf == '' || cpf.length != 11) return false;	
	// Valida 1o digito	
	add = 0;	
	for (i=0; i < 9; i ++)		
		add += parseInt(cpf.charAt(i)) * (10 - i);	
		rev = 11 - (add % 11);	
		if (rev == 10 || rev == 11)		
			rev = 0;	
		if (rev != parseInt(cpf.charAt(9)))		
			return false;		
	// Valida 2o digito	
	add = 0;	
	for (i = 0; i < 10; i ++)		
		add += parseInt(cpf.charAt(i)) * (11 - i);	
	rev = 11 - (add % 11);	
	if (rev == 10 || rev == 11)	
		rev = 0;	
	if (rev != parseInt(cpf.charAt(10)))
		return false;		
	return true;   
}

